import { useState } from 'react';
import {
    Box,
    TextField,
    Button,
} from '@mui/material';
import axios from 'axios';

const endpointMapping = {
    'Notion': 'notion',
    'Airtable': 'airtable',
    'Hubspot': 'hubspot',
};

export const DataForm = ({ integrationType, credentials }) => {
    const [loadedData, setLoadedData] = useState(null);
    const endpoint = endpointMapping[integrationType];

    const handleLoad = async () => {
        try {
            const formData = new FormData();
            formData.append('credentials', JSON.stringify(credentials));
            const response = await axios.post(`http://localhost:8000/integrations/${endpoint}/load`, formData);
            const data = response.data;
            setLoadedData(data);
        } catch (e) {
            alert(e?.response?.data?.detail);
        }
    }

    return (
        <Box 
            display="flex" 
            justifyContent="center" 
            alignItems="center" 
            flexDirection="column" 
            width="90vw" 
            height="90vh" 
            sx={{ p: 5, border: '2px solid #ccc', borderRadius: 3, boxShadow: 4, bgcolor: '#f9f9f9' }}
        >
            <Box display="flex" flexDirection="column" width="100%">
                <TextField
                    label="Loaded Data"
                    value={loadedData ? JSON.stringify(loadedData, null, 2) : ''}
                    sx={{ mt: 2, fontSize: '1.2rem' }}
                    InputLabelProps={{ shrink: true }}
                    disabled
                    multiline
                    rows={20} // Increased rows for more space
                />
                <Button
                    onClick={handleLoad}
                    sx={{ mt: 3, fontSize: '1.2rem', p: 2 }} // Bigger button
                    variant="contained"
                >
                    Load Data
                </Button>
                <Button
                    onClick={() => setLoadedData(null)}
                    sx={{ mt: 2, fontSize: '1.2rem', p: 2, bgcolor: 'red', color: 'white', '&:hover': { bgcolor: 'darkred' } }} // Bigger clear button with red color
                    variant="contained"
                >
                    Clear Data
                </Button>
            </Box>
        </Box>
    );
}
